const partials = {
    "/attribute": "<div class='{{name}} vblock'><label>{{label}}</label><input class='value' name='{{name}}' placeholder='&mdash;' /><input class='modifier' name='{{modifierName}}' placeholder='{{modifierValue}}' /><button class=\"rollButton\" onclick=\"() => {sendMessage(`/r d20 ${doc.values['{{modifierName}}']}`)}\">Roll</button></div>",
    "/character-weapon": "<input name='name' /><input name='damage' /><button onclick=\"() => {sendMessage('/r {{damage}}')}\">Roll</button><input name='qualities' /><input name='weight' />",
    "/hfield": "<div class='{{name}} hblock'><label>{{label}}</label><input name='{{name}}' placeholder='{{placeholder}}' /></div>",
    "/sendMessage": "<script>let sendMessage = (message) => {window.top.postMessage({source: 'Frame',id: '123',message: 'onMessage',data: message},'http://localhost:3000')}\n</script>",
    "/system-style": "<style>* {color: black;}input,textarea,button {background-color: white;color: black;}@media (prefers-color-scheme: dark) {* {color: #eee;}input,textarea,button {background-color: #333 !important;color: white !important;}}body {margin: 0;padding: 20px;}h1 {font-size: 18px;text-transform: uppercase;font-weight: 900;line-height: 20px;padding: 1px;margin: 0;text-align: center;}h2 {position: relative;font-size: 10px;line-height: 30px;color: #aaa;overflow: hidden;text-transform: uppercase;margin-top: 15px;letter-spacing: 1px;overflow: hidden;display: flex;flex-direction: row;}h2:before,h2:after {content: \"\";flex: 1 1;border-bottom: 1px solid #aaa;margin: auto;}h2:before {margin-right: 20px}h2:after {margin-left: 20px}p {margin-top: 10px;}label {font-size: 14px;margin: 10px 0 2px;}input[type=\"text\"] {width: 100%;}input[type=\"text\"],input[type=\"number\"] {border-radius: 8px;border: none;padding: 5px 0 5px 10px;}input[type=\"color\"] {appearance: none;-moz-appearance: none;-webkit-appearance: none;border-radius: 8px;overflow: hidden;width: 30px;height: 30px;border: 0;padding: 0;background: none;cursor: pointer;}::-webkit-color-swatch-wrapper {padding: 0;}::-webkit-color-swatch {border: 0;border-radius: 0;}::-moz-color-swatch,::-moz-focus-inner {border: 0;}::-moz-focus-inner {padding: 0;}input[type=button],button {border-radius: 8px;padding: 5px 10px;border: none;background: #333;color: white;}textarea {width: 100%;border-radius: 8px;border: none;padding: 5px 0 5px 10px;}.grid {display: grid;grid-column-gap: 5px;grid-row-gap: 5px;align-items: center;}\n</style>",
    "/token-and-portrait": "<div style=\"display: flex\"><div style=\"flex: 1 1 auto\"><p>Token:</p><asset name=\"token\"></asset></div><div style=\"flex: 1 1 auto\"><p>Portrait:</p><asset name=\"portrait\"></asset></div></div>",
    "/vfield": "<div class='{{name}} vblock'><label>{{label}}</label><input name='{{name}}' placeholder='{{placeholder}}' /></div>"
};return partials